import { logger } from "../core/logger.js";
const logResult = (result, customLogger = logger, messagePrefix = 'Error:')=>{
    if (result.isErr()) {
        const error = result.error;
        customLogger.error(`${messagePrefix} ${error.message}`, error);
    }
    return result;
};
const logResultAsync = (resultAsync, customLogger = logger, messagePrefix = 'Error:')=>resultAsync.mapErr((error)=>{
        customLogger.error(`${messagePrefix} ${error.message}`, error);
        return error;
    });
export { logResult, logResultAsync };
